    padding-bottom: 60px;
    font-size: 1.1rem;
}

#features {
    background: cadetblue;
    display: block;
    padding-bottom: 60px;
    font-size: 1.1rem;
}

#slackButton {
    height: 75px;
    width: 250px;
    border: none;
    /* background: none; */
    cursor: pointer;
}


#slackButton:hover svg {
    /* transform: rotate(45deg); */
    transform: scale(1.2) rotate(20deg);
}

#slackButton:hover {
    /* transform: rotate(45deg); */
    transform: scale(0.98);
}

#slackButton::-moz-focus-inner {
    border: 0;
}

#slackButton svg {
    outline: none;
    transition: transform 0.3s linear;
    height: 50px;
}


#use {
    padding-bottom: 60px;
    font-size: 1.1rem;
}

#footer {
    background: #2b4251;
