    float: inline-end;
}

#back-to-top {
    background-color: #ffd05a;
    border: 0;
    border-radius: 4px;
    bottom: 1rem;
    box-shadow: 1px 1px 2px rgba(0, 0, 0, .1);
    cursor: pointer;
    overflow: hidden;
    padding: .5rem .5rem;
    position: fixed;
    right: 1rem;
    text-decoration: none;
    transition: transform .3s ease-in-out;
    z-index: 99;
}


#back-to-top img {
    width: 2rem;
}

